import React from 'react';
import AddTodoForm from './AddTodoForm';
import TodoList from './TodoList';
import './style.css';


function App() {

  const [newTodo, setNewTodo] = React.useState("");

  const handleAddTodo = (event) => {
    event.preventDefault();
    let titleTodo = event.target.title.value;
    event.target.reset();
}

//   const searchedTodo = newTodo.filter((todo) =>
//   todo.title.toLowerCase().includes(searchedTodo.toLowerCase())
//   );

  return (
    <div>
      <header>
        <h1>Todo List</h1>
      </header>
        <AddTodoForm onSubmit={handleAddTodo && setNewTodo} />
        <p>{newTodo}</p>      
        <TodoList />
    </div>
  );
}

export default App;
